tags.description:
const Flowee = require('flowee-js-pure'); 

async function main() {
  // Connect
  let flowee = new Flowee();

  // Get version of Flowee we are connected to
  try {
    let version = await flowee.meta.version();
  catch(err) {
    console.log(err);
  }
}

main();

